import { useHistory, Link } from 'react-router-dom';

import Button from 'common/components/button';

import $illustrationIcon from 'common/assets/images/illustration-icon.svg';
import "./dashboard.css";

const Dashboard = () => {
  const history = useHistory();

  const goTo = path => e => {
    e.preventDefault();
    history.push(path);
  }

  return (
    <>
      <div className="grid dashboard-padding">
        <div className="col-1-1">
          <h1>Welcome to Doter</h1>
          <p>Want to see how it works? <Link to="/demo">Show me a quick tour</Link></p>
        </div>
      </div>

      <div className="grid survey-actions-first">
        <div className="col-8-12">
          <div className="create-survey-first">
            <div className="create-survey-first-content">
              <h2>Create your first NPS survey</h2>
              <p>It takes only 60 seconds to start receiving your first insightful survey</p>
              <Button title="Get Started" type="dark" onClick={goTo("/surveys/new")}/>
            </div>
            <img src={$illustrationIcon} alt="Illustration" />
          </div>
        </div>

        <div className="col-4-12">
          <div className="explore-survey-first">
            <h2>Explore Examples</h2>
            <p>See some of the real-world examples from our internal library</p>
            <Button title="See Sample Surveys" type="dark" onClick={goTo("/demo")} />
          </div>
        </div>
      </div>
    </>
  );
};

export default Dashboard;
